namespace NombreMagique.Component
{
    public partial class Game
    {
        private const int life = 5;
        private const int Min = 1;
        private const int Max = 20;

        private int nbMagic; // donne le nbre aléatoire 
        private bool? gameWon; // indique si on gagne ou on perd, oubien le jeu en cour
        private int nbRemainingLifes;
        private int? value;
        private string indice; // qui indique si le nombre est plus grand ou pétit

        protected override void OnInitialized()
