        private void CheckNumber()
        {
            if (value == nbMagic)
            {
                gameWon = true;
                indice = null;
                return;
            }
            nbRemainingLifes--;
            if (nbRemainingLifes <= 0)
            {
                gameWon = false;
                indice = null;
                return;
            }
            if (value > nbMagic) indice = "Le nombre de magique est plus pétit";
            else if (value < nbMagic) indice = "le nombre de magique est plus grand";
